mapper.select:
select * from
(
	select * from proposal where proNum>#{proNum}
	order by proNum asc
)
where rownum=1


select* from
(
	select * from proposal where proNum < #{proNum}
	order by proNum desc
)
where rownum=1

SELECT *
FROM (
SELECT ROWNUM RN, A.*
FROM (
SELECT *
FROM proposal
ORDER BY proNum
) A
)
WHERE RN BETWEEN #{startRow} AND #{endRow}
